var http=require("http");
var fs=require("fs");
http.createServer(function(req,res){
    var url="."+req.url;
    fs.readFile(url,"utf-8",function(error,data){
        if(error){
            res.write("页面找不到")
        }else{
            res.write(data);
        }
        res.end();
    })
}).listen(4050);